import React, {Component} from 'react';
import './plan.css'

class Plan extends Component {

    change() {
        var value = document.getElementById('range').value;
        document.getElementById('value').innerHTML = `${value}%`;
    }


    level() {
        let value = document.getElementById('range').value;
        if (value < 3) {
            document.getElementById('level').innerHTML = '';
            document.getElementById('level').innerText = '定存'
        } else if (3 <= value  && value < 5) {
            document.getElementById('level').innerHTML = '';
            document.getElementById('level').innerText = '投資股票'
        } else {
            document.getElementById('level').innerHTML = '';
            document.getElementById('level').innerText = '789'
        }
    }

  render(){
    return( 
        <main id="app">
                <div className="left">
                    <div className="asset assetNow">
                        <div className="title">資產現況</div><br/>
                        <div className="block">
                            <div className="input col-3 input-effect assetNowPrincipal">
                                <input className="require effect-16" type="text" placeholder="" onKeyUp="value=value.replace(/[^\d]/g,'') "/>
                                <label>現有存款</label>
                                <span className="focus-border"></span>
                            </div>
                            <div className="input col-3 input-effect assetNowDeposit">
                                <input className="effect-16" type="text" placeholder="" onKeyUp="value=value.replace(/[^\d]/g,'') "/>
                                <label>每月固定存款</label>
                                <span className="focus-border"></span>
                            </div>
                        </div>
                    </div>
                    <div className="asset assetFuture">
                        <div className="title">預期未來生活</div><br/>
                        <div className="block">
                            <div className="input col-3 input-effect assetNowPrincipal">
                                <input className="effect-16" type="text" placeholder="" onKeyUp="value=value.replace(/[^\d]/g,'') " />
                                <label>預計幾歲開始工作</label>
                                <span className="focus-border"></span>
                            </div>
                            <div className="input col-3 input-effect assetNowDeposit">
                                <input className="effect-16" type="text" placeholder="" onKeyUp="value=value.replace(/[^\d]/g,'') " />
                                <label>預計幾歲退休</label>
                                <span className="focus-border"></span>
                            </div>
                        </div>
                    </div>
                    <div className="range-slider">
                        <input id="range" type="range" value="2" min="0" max="10" step="0.1" onInput={this.change() + this.level()}
                            onChange={this.change() + this.level()} />
                        <span id="value">2%</span>
                        <div id="level">
                        </div>
                        <div className="chart">chart</div>
                </div>
            </div>
        </main>    

    )
  }
}


export default Plan
