import React, {Component} from 'react';
import './plan.css'

class Plan extends Component {
    constructor(props){
        super(props)
        this.state= {
            assetNowPrincipal:"",
            assetNowDeposit: ""
        }

        // this.handleChange = this.handleChange.bind(this)

    }

    handleInputChange = (event) => {
        let {name, value} = event.target
        this.setState({
            [name]: value
        })
    }

    handleKeyup = (event) => {
        let {name, value} = event.target
        value = value.replace(/[^\d]/g,'')
        this.setState({
            [name]: value
        })
    }

    // onKeyUp={value = value.replace(/[^\d]/g,'') }


    render(){
        const {assetNowPrincipal, assetNowDeposit} = this.state
        return( 
            <main id="plan">
                    <div className="left">
                        <div className="asset assetNow">
                            <div className="title">資產現況</div><br/>
                            <div className="block">
                                <div className="input col-3 input-effect assetNowPrincipal">
                                    <input name="assetNowPrincipal" 
                                           className="require effect-16" 
                                           type="text"   
                                           value={this.state.assetNowPrincipal} 
                                           onChange={this.handleInputChange} 
                                           onKeyUp={this.handleKeyup}/>
                                    <label>現有存款</label>
                                    <span className="focus-border"></span>
                                </div>
                                <div className="input col-3 input-effect assetNowDeposit">
                                    <input name="assetNowDeposit" 
                                           className="effect-16" 
                                           type="text"  
                                           value={this.state.assetNowDeposit} 
                                           onChange={this.handleInputChange} />
                                    <label>每月固定存款</label>
                                    <span className="focus-border"></span>
                                </div>
                            </div>
                        </div>
                        <div className="asset assetFuture">
                            <div className="title">預期未來生活</div><br/>
                            <div className="block">
                                <div className="input col-3 input-effect assetNowPrincipal">
                                    <input className="effect-16" type="text" />
                                    <label>預計幾歲開始工作</label>
                                    <span className="focus-border"></span>
                                </div>
                                <div className="input col-3 input-effect assetNowDeposit">
                                    <input className="effect-16" type="text" />
                                    <label>預計幾歲退休</label>
                                    <span className="focus-border"></span>
                                </div>
                            </div>
                        </div>
                        <div className="range-slider">
                            <input id="range" type="range" value="2" min="0" max="10" step="0.1"/>
                            {/* onInput={() => {this.change; this.level}}
                                onChange={() => {this.change; this.level}}  */}
                            <span id="value">2%</span>
                            <div id="level">
                            </div>
                            <div className="chart">chart</div>
                    </div>
                </div>
            </main>    

        )
    }
}



export default Plan
